    /*
     * Editing a user_personal
     */
    function edit($Id)
    {   
        // check if the user_personal exists before trying to edit it
        $data['user_personal'] = $this->User_personal_model->get_user_personal($Id);
        
        if(isset($data['user_personal']['Id']))
        {
            if(isset($_POST) && count($_POST) > 0)     
