     * Deleting user_personal
     */
    function remove($Id)
    {
        $user_personal = $this->User_personal_model->get_user_personal($Id);

        // check if the user_personal exists before trying to delete it
        if(isset($user_personal['Id']))
        {
            $this->User_personal_model->delete_user_personal($Id);
